

library(readr)
library(readxl)
library(stringr)
library(ngram)
library(xlsx)

Pre1996 <- read_xlsx("/FED-Communications-Project/Data_mining/Pre1996/Pre1996_speeches.xlsx")


Post1996 <- read_xlsx("/FED-Communications-Project/Data_mining/1996-today/fed_speeches_1996_2021.xlsx")

Little <- read_xlsx("/FED-Communications-Project/Data_mining/Little_guys/little_guys_new.xlsx")


colnames(Pre1996)
colnames(Post1996)
colnames(Little)


Pre1996 <- Pre1996[,c("author","date","content", "location")]
colnames(Pre1996) <- c("author","date","text", "location")

head(Pre1996$date)

Pre1996$date <- as.Date(Pre1996$date, format ='%B %d, %Y')


Post1996 <- Post1996[,c("speaker","date","text","location")]
colnames(Post1996) <- c("author","date","text", "location")



head(Post1996$date)
Post1996$date <- paste0(substr(Post1996$date,0,4),"-",substr(Post1996$date,5,6),"-", substr(Post1996$date,7,8))



Little <- Little[,c("author","date", "text", "location")]

Little$date <- as.Date(as.numeric(Little$date), origin="1899-12-30")
head(Little$date)

Pre1996$Fed_President <- paste('No')
Post1996$Fed_President <- paste('No')
Little$Fed_President <- paste('Yes')


Master <- rbind(Pre1996, Post1996, Little)
Master$date <-as.Date(Master$date)
head(Master)


table(Master$author)
unique(Master$author)

min(Master$date, na.rm = TRUE)
Check_1 <- subset(Master, Master$date=="1905-04-30")

Check_1$text
Master <- Master[with(Master, order(Master$date, decreasing = TRUE)),]

Master <- subset(Master,Master$date>"1949-03-21")

max(Master$date)

table(Master$location)

